fragment DenestedSelections on DenestedOfSelectionItem {
  depth
  item {
    selection {
      text
      __typename
      ... on GraphQLFieldSelection {
        name
        alias
      }
      ... on GraphQLFragmentSpreadSelection {
        fragmentName
      }
      ... on GraphQLInlineFragmentSelection {
        typeName
      }
    }
  }
}

