query ReadTextFileWithVersion($textFilePath: String!, $fileVersion: FileVersion) {
  readTextFile(textFilePath: $textFilePath, fileVersion: $fileVersion)
}

query GetSchema($botFilePath: String!) {
  botSchema(botFilePath: $botFilePath)
  botSpec(botFilePath: $botFilePath) {
    consumedSchemaPath
    excludeConfigurationFromConsumedSchema
    providedSchemaPath
  }
}

query ParseGraphQLSchemaAndOperations($graphql: [AdditionalFileInput!]!) {
  graphQL(additionalFiles: $graphql) {
    objectTypes {
      interfaces
      name
      fields {
        name
        parameters {
          name
          type {
            text
          }
        }
        type {
          text
        }
      }
    }
    inputObjectTypes {
      name
      fields {
        name
        type {
          text
        }
      }
    }
    interfaceTypes {
      name
      fields {
        name
        parameters {
          name
          type {
            text
          }
        }
        type {
          text
        }
      }
    }
    enumerations {
      name
      values {
        name
      }
    }
    fragments {
      name
      text
      typeCondition
      variables {
        name
        type {
          text
        }
      }
      denestedSelections {
        ... DenestedSelections
      }
    }
    operations {
      name
      operationType
      text
      variables {
        name
        type {
          text
        }
      }
      denestedSelections {
        ... DenestedSelections
      }
    }
  }
}

